def define_martian_year(df, time_col_name):
    mars_timestamps = {k: pd.to_datetime(v) for k, v in mars_years.items()}
    df["MY"] = 0
    for yr, t in mars_timestamps.items():
        df.loc[df[time_col_name] > t, "MY"] = yr

